import styled from "styled-components";
import { Typography, Radio, Input } from "antd";
const { Title, Paragraph, Text } = Typography;

function Question({ question, answers }) {
  const [isAnswered, setAnswered] = React.useState(false);
  const [isCorrect, setCorrect] = React.useState(false);
  const [comment, setComment] = React.useState('');

  const handleAnswer = (e) => {
    const { value } = e.target;
    setAnswered(true);
    const answer = answers.find(answer => answer.id === value);
    const isAnswerCorrect = answer.isCorrect;
    const answerComment = answer.comment;
    setCorrect(isAnswerCorrect);
    setComment(answerComment);
  };

  const shuffle = array => {
    return array.sort(() => Math.random() - 0.5);
  };

  const radioStyle = {
    display: "block",
    height: "30px",
    lineHeight: "30px"
  };

  const answersArr = shuffle(answers);

  return (
    <Wrapper>
      <Title level={2}>{question}</Title>
      <Radio.Group onChange={handleAnswer}>
        {answersArr.map(element => {
          return (
            <Radio key={element.id} style={radioStyle} value={element.id}>
              {element.answer}
            </Radio>
          );
        })}
      </Radio.Group>
      <div>
      {isAnswered && (isCorrect && comment)}
      </div>
    </Wrapper>
  );
}

export default Question;

const Wrapper = styled.div`
  margin: 24px 0;
`;
