import styled from "styled-components";
import { Typography, Radio, Input } from "antd";
const { Title, Paragraph, Text } = Typography;

function Question({
  question,
  answers
}) {
  const [isAnswered, setAnswered] = React.useState(false);
  const [isCorrect, setCorrect] = React.useState(false);

  const handleAnswer = (index, wrong) => {
    setAnswered(true);
  };

  const shuffle = (array) => {
    return array.sort(() => Math.random() - 0.5);
  }

  const radioStyle = {
    display: "block",
    height: "30px",
    lineHeight: "30px"
  };

  const answersArr = shuffle(answers);
  console.log('---', answersArr);

  return (
    <Wrapper>
      <Title level={2}>{question}</Title>
      <Radio.Group onChange={handleAnswer}>
        {/* {answersArr.map(element => {
        <Radio style={radioStyle} value={element.comment}>
          {element.answer}
        </Radio>
        })} */}
      </Radio.Group>
      {/* {isAnswered && (isCorrect ? wrongAnswerComment : correctAnswerComment)} */}
    </Wrapper>
  );
}

export default Question;

const Wrapper = styled.div`
  margin: 24px 0;
`;
