import styled from "styled-components";
import { Modal, Row, Typography, Button } from "antd";
import Question from "../components/Question";
import Card from "../components/Card";
import { useEffect } from "react";

const { Title, Paragraph, Text } = Typography;

const data = [
  {
    id: "1",
    question: "Начну с простого: Вы фанзиль?",
    answers: [
      {
        id: 1,
        answer: "Да",
        comment: "Пизда. Фанзиль не открылся бы так легко.",
        isCorrect: false
      },
      {
        id: 2,
        answer: "Нет",
        comment: "Seems fair, продолжаем.",
        isCorrect: true
      }
    ]
  },
];

function Home() {
  const [counter, increment] = React.useState(0);
  const [correctAnswers, incrementCorrectAnswers] = React.useState(0);
  const [state, setState] = React.useState(null);

  useEffect(() => {
    if (counter === 2) {
      setState("finished");
    }
  }, [counter]);

  return (
    <Container>
      <Title>Вы Фанзиль?</Title>

      <Wrapper>
        {state !== "finished" && (
          <Row>
            <Paragraph>
              «Вы Фанзиль?» — это универсальный тест на определение того, проходит тест фанзиль или же нет. Результат теста небинарный — вы можете оказаться кем-то очень похожим на фанзиля, или же, если вы <Text strong>нормальный</Text> человек, совсем на него не похожим.
            </Paragraph>
            <Paragraph>
              По результатам теста вы сможете сделать выводы о своей личности и о том, что
            </Paragraph>
          </Row>
        )}

        {state === null && (
          <Row>
            <Button type='primary' onClick={() => setState("started")}>
              Начать
            </Button>
          </Row>
        )}

        {state !== null && (
          <Row>
            {data.map(item => (
              <Question
                key={item.id}
                {...item}
                counter={counter}
                updateCounter={increment}
                correctAnswers={correctAnswers}
                updateCorrectCounter={incrementCorrectAnswers}
              />
            ))}
          </Row>
        )}
      </Wrapper>

      {state === "finished" && <Card results={[counter, correctAnswers]} />}
    </Container>
  );
}

const Container = styled.main`
  margin: 0 auto;
  padding: 64px 32px 0;
  max-width: 1024px;
  width: 100%;
`;

const Wrapper = styled.div``;

export default Home;
