import styled from "styled-components";
import { Modal, Row, Typography, Button } from "antd";
import Question from "../components/Question";
import Card from "../components/Card";
import { useEffect } from "react";

const { Title, Paragraph, Text } = Typography;

const data = [
  {
    id: "1",
    question: "Начну с простого: Вы фанзиль?",
    answers: [
      {
        id: 1,
        answer: "Да",
        comment: "Пизда. Фанзиль не открылся бы так легко.",
        isCorrect: false
      },
      {
        id: 2,
        answer: "Нет",
        comment: "Seems fair, продолжаем.",
        isCorrect: true
      }
    ]
  },
];

function Home() {
  const [counter, increment] = React.useState(0);
  const [correctAnswers, incrementCorrectAnswers] = React.useState(0);
  const [state, setState] = React.useState(null);

  useEffect(() => {
    if (counter === 2) {
      setState("finished");
    }
  }, [counter]);

  return (
    <Container>
      {/* <Title>Вы Фанзиль?</Title> */}
      <Title>flamrous</Title>

      <Wrapper>
        {state !== "finished" && (
          <Row>
            <Paragraph>
              «Вы Фанзиль?» — это универсальный психологический тест на определение того, насколько вы похожи на фанзиля, титана современности, известного своей тревожностью, токсичностью и любовью к аниме. Результат теста небинарный — вы можете оказаться кем-то очень похожим на фанзиля, или же, если вы <Text strong>нормальный</Text> человек, совсем на него не похожим.
            </Paragraph>
            <Paragraph>
              Что делать с результатами теста — дело ваше. Поскольку я, гениальный создатель этого теста, — ваш союзник, то по его результатам я оставлю совет, как лучше всего интерпретировать результаты и что делать дальше.
            </Paragraph>
          </Row>
        )}

        {state === null && (
          <Row>
            <Button type='primary' onClick={() => setState("started")}>
              Поехали
            </Button>
          </Row>
        )}

        {state !== null && (
          <Row>
            {data.map(item => (
              <Question
                key={item.id}
                {...item}
                counter={counter}
                updateCounter={increment}
                correctAnswers={correctAnswers}
                updateCorrectCounter={incrementCorrectAnswers}
              />
            ))}
          </Row>
        )}
      </Wrapper>

      {state === "finished" && <Card results={[counter, correctAnswers]} />}
    </Container>
  );
}

const Container = styled.main`
  margin: 0 auto;
  padding: 64px 32px 0;
  max-width: 1024px;
  width: 100%;
`;

const Wrapper = styled.div``;

export default Home;
