import styled from "styled-components";
import { Modal, Row, Typography, Button } from "antd";
import Question from "../components/Question";
import Card from "../components/Card";
import { useEffect } from "react";

const { Title, Paragraph } = Typography;

const data = [
  {  
    "id": "1",
    "question": "Начну с простого: Вы фанзиль?",
    "answers": [
      {
        "id": 1,
        "answer": "Да",
        "comment": "Пизда. Фанзиль не открылся бы так легко.",
        "isCorrect": false
      },
      {
        "id": 2,
        "answer": "Нет",
        "comment": "Seems fair, продолжаем.",
        "isCorrect": true
      }
    ]
  },
  {
    "id": "2",
    "question": "Вы поссорились со своей девушкой, спустя какое-то время вы разговариваете на улице и она говорит, что ей холодно. Ваши действия?",
    "answers": [
      {
        "id": 1,
        "answer": "Предложу свою куртку / обниму её.",
        "comment": "Похоже на то, что у вас нет ни единого шанса сдать этот тест.",
        "isCorrect": false
      },
      {
        "id": 2,
        "answer": "Предложу ей пойти вон в то соседнее здание, там наверняка теплее.",
        "comment": "Абсолютно верно. Законы романтики неведомы человеку рациональности.",
        "isCorrect": true
      }
    ]
  },
];

function Home() {
  const [counter, increment] = React.useState(0);
  const [correctAnswers, incrementCorrectAnswers] = React.useState(0);
  const [state, setState] = React.useState(null);

  useEffect(() => {
    if (counter === 2) {
      setState("finished");
    }
  }, [counter]);

  return (
    <Container>
      <Title>Насколько вы Фанзиль?</Title>

      <Wrapper>
        {state !== "finished" && (
          <Row>
            <Paragraph>
              Flameous loser DON'T LOOK AT MY SCREEN EVER AGAIN YOU FUCKING
              BASTARD I FOCKEN HATE YOU I FOCKEN HATE YOU I FOCKEN HATE YOU I
              FOCKEN HATE YOU I FOCKEN HATE YOU I FOCKEN HATE YOU I FOCKEN HATE
              YOU I FOCKEN HATE YOU
            </Paragraph>
          </Row>
        )}

        {state === null && (
          <Row>
            <Button type='primary' onClick={() => setState("started")}>
              Начать
            </Button>
          </Row>
        )}

        {state === 'started' && (
          <Row>
            {data.map(item => (
              <Question
                key={item.id}
                {...item}
                counter={counter}
                updateCounter={increment}
                correctAnswers={correctAnswers}
                updateCorrectCounter={incrementCorrectAnswers}
              />
            ))}
          </Row>
        )}
      </Wrapper>

      {state === "finished" && <Card results={[counter, correctAnswers]} />}
    </Container>
  );
}

const Container = styled.main`
  margin: 0 auto;
  padding: 64px 32px 0;
  max-width: 1024px;
  width: 100%;
`;

const Wrapper = styled.div``;

export default Home;
