import styled from "styled-components";
import { Row, Typography, Divider } from "antd";
const { Title, Paragraph, Text } = Typography;

function Question({
  question,
  wrongAnswer,
  wrongAnswerComment,
  correctAnswer,
  correctAnswerComment
}) {
  return (
    <Wrapper>
      <Title level={2}>{question}</Title>
      {wrongAnswer}
      <br />
      {correctAnswer}
      <br />
      {wrongAnswerComment}, {correctAnswerComment}
    </Wrapper>
  );
}

export default Question;

const Wrapper = styled.div`
  margin: 24px 0;
`;
